mapper.select:
SELECT class_category_id, business_account_id, class_type, class_category_name, description, is_active
FROM MASTER_CLASSCATEGORY
WHERE class_type = #{categoryType}

    AND business_account_id = #{businessAccountId}

ORDER BY class_category_id

SELECT class_category_id, business_account_id, class_type, class_category_name, description, is_active
FROM MASTER_CLASSCATEGORY
WHERE class_category_id = #{categoryId}

SELECT class_category_id, business_account_id, class_type, class_category_name, description, is_active
FROM MASTER_CLASSCATEGORY
WHERE class_category_id = #{categoryId}

    AND business_account_id = #{businessAccountId}

mapper.update:
UPDATE MASTER_CLASSCATEGORY
SET class_type = #{categoryType},
    class_category_name = #{categoryName},
    description = #{description},
    is_active = #{isActive}
WHERE class_category_id = #{categoryId}

    AND business_account_id = #{businessAccountId}

mapper.delete:
DELETE FROM MASTER_CLASSCATEGORY
WHERE class_category_id = #{categoryId}

    AND business_account_id = #{businessAccountId}
