mapper.select:
SELECT seller_id, business_account_id, seller_name, contact_info, address, phone_number, email, is_active
FROM MASTER_SELLER
WHERE seller_id = #{sellerId}

SELECT seller_id, business_account_id, seller_name, contact_info, address, phone_number, email, is_active
FROM MASTER_SELLER
WHERE seller_id = #{sellerId}

    AND business_account_id = #{businessAccountId}

mapper.update:
UPDATE MASTER_SELLER
SET seller_name = #{sellerName},
    contact_info = #{contactInfo},
    address = #{address},
    phone_number = #{phoneNumber},
    email = #{email},
    is_active = #{isActive}
WHERE seller_id = #{sellerId}

    AND business_account_id = #{businessAccountId}

mapper.delete:
DELETE FROM MASTER_SELLER
WHERE seller_id = #{sellerId}

    AND business_account_id = #{businessAccountId}
