mapper.select:
SELECT supplier_id, business_account_id, supplier_name, contact_info, address, phone_number, email, is_active
FROM MASTER_SUPPLIER
WHERE supplier_id = #{supplierId}

SELECT supplier_id, business_account_id, supplier_name, contact_info, address, phone_number, email, is_active
FROM MASTER_SUPPLIER
WHERE supplier_id = #{supplierId}

    AND business_account_id = #{businessAccountId}

mapper.update:
UPDATE MASTER_SUPPLIER
SET supplier_name = #{supplierName},
    contact_info = #{contactInfo},
    address = #{address},
    phone_number = #{phoneNumber},
    email = #{email},
    is_active = #{isActive}
WHERE supplier_id = #{supplierId}

    AND business_account_id = #{businessAccountId}

mapper.delete:
DELETE FROM MASTER_SUPPLIER
WHERE supplier_id = #{supplierId}

    AND business_account_id = #{businessAccountId}
