mapper.select:
SELECT id, user_id, business_account_id, role, is_default, create_date, update_date
FROM USER_BUSINESS_ACCOUNT_MEMBERSHIP
WHERE user_id = #{userId} AND business_account_id = #{businessAccountId}

mapper.update:
UPDATE USER_BUSINESS_ACCOUNT_MEMBERSHIP
SET role = #{role},
    is_default = #{isDefault}
WHERE id = #{id}

UPDATE USER_BUSINESS_ACCOUNT_MEMBERSHIP
SET is_default = FALSE
WHERE user_id = #{userId}

UPDATE USER_BUSINESS_ACCOUNT_MEMBERSHIP
SET is_default = TRUE
WHERE user_id = #{userId} AND business_account_id = #{businessAccountId}
